<?phpclass autocomplete extends Controller {    function __construct() {        parent::Controller();    }    	function index() {		$action = (!empty($_REQUEST['action'])) ? $_REQUEST['action'] : '';		$namelike = (!empty($_REQUEST['q'])) ? $_REQUEST['q'] : '';				$result = '';		if ($action == 'pegawai') {			$param_pegawai = array(				'NAMA' => $namelike,				'SORTING' => 1,				'PencarianDetail' => 0,				'K_JENIS_KERJA' => 'x',				'K_UNIT_KERJA' => 'x'			);			$array_pegawai = $this->lpegawai->GetArrayPegawai($param_pegawai);						foreach ($array_pegawai['Pegawai'] as $row) {				$result .= $row['K_PEGAWAI']."|".$row['NAMA']."\n";			}		}				echo $result;	}}?>